const IMG_API = "https://image.tmdb.org/t/p/w1280";

const setVoteClass =(vote) =>{
    if(vote >= 8){
        return "green";
    }else if ( vote >= 6){
        return "orange";
    }else{
        return "red";
    }
};


const Movie = (props) => {

    let {title, poster_path, id,vote_average,overview} = props.movieDatas
    let favContext = useContext(FavoriteContext);
    let [isFav, setFav] = useState('');

    useEffect(
        () => {
            let find = false;
