                }
                find = true;
                setFav(' active')
                break; // met fin a la boucle
            }
            if (!find) {
                setFav('')
            }
        }, [favContext]
    )
    return(
    <div className="movie">

        <img src={IMG_API + poster_path} alt={title}/>
        <div className="movie-info">
